import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import { Container, Typography, Paper } from "@material-ui/core";
import { LanguageGraph } from './LanguageGraph'


import avatar_dark from './Avatar_dark.svg'
import avatar_light from './Avatar_light.svg'

const useStyles = makeStyles((theme) => ({
  heroContent: {
    minHeight: 800,
    padding: theme.spacing(8, 0, 5),
    backgroundColor: theme.palette.primary.dark 

  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  avatar: {
    width:200,
    height:200
  },
  circle: {
    margin:'auto',
    // opacity: '25%',
    // background: 'white',
    background: 'lavender',
    borderRadius: '50%',
    width: 220,
    height: 220,
  },
  caption:{
    margin: theme.spacing(4)
  },
  parent:{
    display: 'grid',
    gridTemplateColumns: '250px 1fr',
    gridTemplateRows: '150px 1fr'
  },
  child:{
      gridArea: '1 / 1 / 2 / 2'
  }
}));

function HeroContent({
  gitHubData,
  darkState
}) {
  const classes = useStyles();

  return (
    <div className={classes.heroContent}>
      <Container maxWidth="md">
        <div className={classes.parent}>
          <LanguageGraph className={classes.child}/>
        <Paper className={classes.circle}>

          {
            darkState
            ? <img src={avatar_dark} className={classes.avatar}/>
            : <img src={avatar_light} className={classes.avatar}/>
          }        
        </Paper>
        </div>
