import { createMuiTheme, ThemeProvider } from "@material-ui/core/styles";
import { useState } from "react";
import { makeStyles } from '@material-ui/core/styles';

import CssBaseline from '@material-ui/core/CssBaseline';
import { Link, Container, Typography, Toolbar, Grid, AppBar } from "@material-ui/core";
import { blue, lightBlue, purple, teal } from "@material-ui/core/colors";
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Brightness7Icon from '@material-ui/icons/Brightness7';
import Brightness4Icon from '@material-ui/icons/Brightness4';
import {ProjectCard} from './ProjectCard'
import {HeroContent} from './HeroContent'


function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  icon: {
    marginRight: theme.spacing(2),
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },

  footer: {
    padding: theme.spacing(6),
  },
  toolbarButtons: {
    marginLeft: 'auto',
  },
}));

const projects = [1, 2, 3];

const Main = () => {
  const [darkState, setDarkState] = useState(false);
  const palletType = darkState ? "dark" : "light"; 
  const mainPrimaryColor = darkState ? purple[600] : blue[500];
  const mainSecondaryColor = darkState ? purple[200] : lightBlue[500];
  const darkTheme = createMuiTheme({
    palette: {
      type: palletType,
      primary: {
        main: mainPrimaryColor
      },
      secondary: {
        main: mainSecondaryColor
      },
      textPrimary:{
        main:'#000'
      },
    }
  });
  const handleThemeChange = () => {
    setDarkState(!darkState);
  };
  const classes = useStyles();

  return (
    <>
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" color="inherit" /* noWrap */>
            James Welsh
          </Typography>
          <div className={classes.toolbarButtons}>
{/*             <FormControlLabel 
            value="dark_mode"
            label="Dark Mode"
            control={<Switch
              checked={darkState} 
              onChange={handleThemeChange} 
              />}
            labelPlacement="start"
            /> */}
            <IconButton onClick={() => handleThemeChange()}>
              {darkState ? <Brightness4Icon/> : <Brightness7Icon/>}
            </IconButton>
            </div>
        </Toolbar>
      </AppBar>
      <main>
        {/* Hero unit */}
        <HeroContent/>
        <Container className={classes.cardGrid} maxWidth="md">
          {/* End hero unit */}
          <Grid container spacing={4}>
            {projects.map((card) => (
              <Grid item key={card} xs={12} sm={6} md={4}>
                <ProjectCard/>
              </Grid>
            ))}
          </Grid>
        </Container>
      </main>

      {/* Footer */}
      <footer className={classes.footer}>
        <Typography variant="h6" align="center" gutterBottom>
          James Welsh
        </Typography>
        <Typography variant="subtitle1" align="center" component="p">
          Living the dream!
        </Typography>
        <Copyright />
      </footer>
      {/* End footer */}
      </div>
      </ThemeProvider>
    </>
  );
}


export { Main }
